package doWhileLoop;

import java.util.Scanner;

public class WhileDoLoopExersize {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=0;
		

	 
	do {
		a++;
		System.out.print(a);
	
	}while(a!=10);
	
	
	do{
		a--;
		System.out.println("           "+a);
		
	}while(a!=0);
  }
}